DECLARE
    CURSOR c_contratos IS
        SELECT ID_contrato, ID_empleado, descripcion, fecha_inicio, fecha_fin, tipo, salario
        FROM Contrato;
        
    v_id_nomina VARCHAR2(20);
    v_id_empleado VARCHAR2(20);
    v_fecha DATE;
    v_salario_base NUMBER(8,2);
    v_remuneracion_variable NUMBER(8,2);
    v_num_nominas NUMBER;
    v_fecha_inicio DATE;
    v_fecha_fin DATE;
    v_tipo VARCHAR2(20);
    v_salario NUMBER(10,2);
BEGIN
    FOR contrato IN c_contratos LOOP
        v_id_empleado := contrato.ID_empleado;
        v_fecha_inicio := contrato.fecha_inicio;
        v_fecha_fin := contrato.fecha_fin;
        v_tipo := contrato.tipo;
        v_salario := contrato.salario;
        
        -- Determinar el número de nóminas a generar (entre 12 y 36)
        v_num_nominas := ROUND(DBMS_RANDOM.VALUE(12, 36));
        
        -- Calcular el salario base mensual y la remuneración variable
        v_salario_base := ROUND(v_salario / 12, 2);
        v_remuneracion_variable := ROUND(v_salario_base * DBMS_RANDOM.VALUE(0.5, 1), 2); -- Variable remuneration between 50% to 100% of the base salary
        
        FOR i IN 1..v_num_nominas LOOP
            v_id_nomina := 'NOM-' || LPAD(TO_CHAR(DBMS_RANDOM.VALUE(1, 99999)), 5, '0');
            v_fecha := ADD_MONTHS(v_fecha_inicio, i - 1);
            
            IF v_fecha_fin IS NOT NULL AND v_fecha > v_fecha_fin THEN
                EXIT;
            END IF;
            
            -- Insertar la nómina
            VALUES (v_id_nomina, v_id_empleado, v_fecha, v_salario_base, v_remuneracion_variable);
        END LOOP;
    END LOOP;
END;
/
-- Inserts for 30 Materials


DECLARE
    CURSOR c_empleados IS
        SELECT 'EMP-' || LPAD(LEVEL, 5, '0') AS ID_empleado
        FROM DUAL
        CONNECT BY LEVEL <= 100;
    
    v_material_id VARCHAR2(20);
    v_employee_id VARCHAR2(20);
    v_assignment_date DATE;
    v_num_materials NUMBER;
BEGIN
    v_assignment_date := SYSDATE;

    FOR empleado_rec IN c_empleados LOOP
        v_employee_id := empleado_rec.ID_empleado;
        v_num_materials := ROUND(DBMS_RANDOM.VALUE(1, 5)); 

        FOR j IN 1..v_num_materials LOOP
            v_material_id := 'MAT-' || LPAD(FLOOR(DBMS_RANDOM.VALUE(1, 31)), 5, '0');

            BEGIN
                VALUES (v_material_id, v_employee_id, v_assignment_date, NULL);
            EXCEPTION
                WHEN DUP_VAL_ON_INDEX THEN
                    NULL; 
            END;
        END LOOP;
    END LOOP;
END;
/

DECLARE
    CURSOR c_contratos IS
        SELECT ID_contrato, ID_empleado, fecha_inicio, fecha_fin, tipo
        FROM Contrato;
        
    v_fecha_inicio DATE;
    v_fecha_fin DATE;
    v_duracion NUMBER;
    v_num_periodos NUMBER;
    v_id_vacaciones VARCHAR2(20);
    v_counter NUMBER := 1;
    
BEGIN
    FOR contrato IN c_contratos LOOP
        v_num_periodos := ROUND(DBMS_RANDOM.VALUE(1, 4));

        FOR i IN 1..v_num_periodos LOOP
            v_duracion := ROUND(DBMS_RANDOM.VALUE(1, 6));

            IF contrato.tipo = 'Indefinido' THEN
                -- Para contratos indefinidos, la fecha de inicio de vacaciones debe ser mayor a la fecha de inicio del contrato
                v_fecha_inicio := contrato.fecha_inicio + TRUNC(DBMS_RANDOM.VALUE(1, 366));
                v_fecha_fin := v_fecha_inicio + v_duracion - 1;
            ELSE
                -- Para contratos formativos, la fecha de inicio de vacaciones debe estar dentro del rango del contrato
                v_fecha_inicio := contrato.fecha_inicio + TRUNC(DBMS_RANDOM.VALUE(0, contrato.fecha_fin - contrato.fecha_inicio - v_duracion + 1));
                v_fecha_fin := v_fecha_inicio + v_duracion - 1;
            END IF;

            -- Verificar que las fechas generadas sean válidas
            IF v_fecha_inicio IS NOT NULL AND v_fecha_fin IS NOT NULL AND v_fecha_inicio <= v_fecha_fin THEN
                v_id_vacaciones := 'VAC-' || LPAD(v_counter, 5, '0');

                VALUES (v_id_vacaciones, contrato.ID_empleado, v_fecha_inicio, v_fecha_fin);
                v_counter := v_counter + 1;
            END IF;
        END LOOP;
    END LOOP;
END;
